// Extract messages from Slack API response
const slackResponse = $input.first().json;

if (!slackResponse.ok) {
  throw new Error('Slack API Error: ' + slackResponse.error);
}

const messages = slackResponse.messages || [];

if (messages.length === 0) {
  throw new Error('âŒ No messages! Check bot is in channel C0A72CZ6KUP');
}

// Format for AI
const formatted = messages.map(msg => {
  const ts = new Date(parseFloat(msg.ts) * 1000);
  const time = ts.toLocaleString('en-IN', {timeZone: 'Asia/Kolkata'});
  const user = msg.user || 'Unknown';
  const text = (msg.text || '').replace(/<[^>]*>/g, '');
  return `[${time}] ${user}: ${text}`;
}).reverse().join('\n');

// Build Groq request
const groqBody = {
  model: "llama-3.3-70b-versatile",
  messages: [
    {role: "system", content: "You analyze Slack chats. Output ONLY JSON."},
    {role: "user", content: `Analyze these messages:\n\n${formatted}\n\nProvide JSON with:\n- summary: Brief 2-3 sentence summary\n- key_topics: Array of main topics\n- action_items: Array of tasks\n- urgent_items: Array of urgent issues\n- urgency_score: 1-10 (8+ = urgent)\n- call_script: Natural 40-word phone message starting with 'Hello, E-Secretary here.'`}
  ],
  response_format: {type: "json_object"}
};

return {json: {msg_count: messages.length, groq_body: groqBody}};

// Parse AI + Format Beautiful SMS
const ai = $input.first().json;
const prevData = $('2. Format & Build1').first().json;
let parsed = {};

try {
  parsed = JSON.parse(ai.choices[0].message.content);
} catch(e) {
  parsed = {summary: 'Parse error', key_topics: [], action_items: [], urgent_items: [], urgency_score: 5, call_script: 'Error'};
}

// Build BEAUTIFUL ASCII SMS
const now = new Date().toLocaleString('en-IN', {dateStyle: 'medium', timeStyle: 'short'});
let sms = '*** E-SECRETARY REPORT ***\n';
sms += '============================\n';
sms += `>> Date: ${now}\n`;
sms += `>> Messages: ${prevData.msg_count}\n`;
sms += `>> Urgency: ${parsed.urgency_score}/10\n`;
sms += '============================\n\n';

sms += '--- SUMMARY ---\n' + parsed.summary + '\n\n';

if (parsed.key_topics?.length) {
  sms += '--- KEY TOPICS ---\n';
  parsed.key_topics.slice(0,3).forEach(t => {sms += `  * ${t}\n`;});
  sms += '\n';
}

if (parsed.urgent_items?.length) {
  sms += '!!! URGENT ITEMS !!!\n';
  parsed.urgent_items.forEach(u => {sms += `  >> ${u}\n`;});
  sms += '\n';
}

if (parsed.action_items?.length) {
  sms += '--- ACTION ITEMS ---\n';
  parsed.action_items.forEach(a => {sms += `  [ ] ${a}\n`;});
  sms += '\n';
}

sms += '============================\n';
sms += '~ E-Secretary AI ~';

const finalSms = sms.length > 1550 ? sms.substring(0, 1547) + '...' : sms;
const cleanCall = (parsed.call_script || 'Error').replace(/[*#<>]/g, '');

return {json: {sms_message: finalSms, call_message: cleanCall}};
